_SLOW_CUT = 15
_rng = np.random.default_rng(20250912)

def _offdiag_values(mat, rows, cols):
    sub = mat[np.ix_(rows, cols)].copy()
    if set(rows) == set(cols):
        for i, r in enumerate(rows):
            for j, c in enumerate(cols):
                if r == c:
                    sub[i, j] = np.nan
    vals = sub[~np.isnan(sub)]
    vals = vals[vals != 0.0]
    return vals

_slow = list(range(_SLOW_CUT))
_fast = list(range(_SLOW_CUT, bandwidth_60.shape[0]))

_pool_sf = _offdiag_values(bandwidth_60, _slow, _fast)
_pool_fs = _offdiag_values(bandwidth_60, _fast, _slow)
_pool_ff = _offdiag_values(bandwidth_60, _fast, _fast)
_pool_ss = _offdiag_values(bandwidth_60, _slow, _slow)

def _expand_matrix(base_mat: np.ndarray, target_n: int, slow_cut: int = 15) -> np.ndarray:
    N0 = base_mat.shape[0]
    out = np.zeros((target_n, target_n), dtype=float)
    out[:N0, :N0] = base_mat
    for i in range(target_n):
        for j in range(target_n):
            if i == j:
                continue
            if i < N0 and j < N0:
                continue
            i_is_slow = (i < slow_cut)
            j_is_slow = (j < slow_cut)
            if i_is_slow and j_is_slow:
                pool = _pool_ss
            elif i_is_slow and (not j_is_slow):
                pool = _pool_sf
            elif (not i_is_slow) and j_is_slow:
                pool = _pool_fs
            else:
                pool = _pool_ff
            val = float(_rng.choice(pool))
            out[i, j] = round(val, 1)
    return out

bandwidth_64  = _expand_matrix(bandwidth_60, 64, slow_cut=_SLOW_CUT)
bandwidth_128 = _expand_matrix(bandwidth_60, 128, slow_cut=_SLOW_CUT)

def save_with_commas_and_brackets(path: Path, varname: str, arr: np.ndarray):
    # NOTE: comment translated from Chinese
    s = np.array2string(arr, separator=", ", formatter={"float_kind": lambda x: f"{x:.1f}"},
    threshold=np.inf)
    text = f"{varname} = {s}\n"
    path.write_text(text, encoding="utf-8")

if __name__ == "__main__":
    # quick sanity checks
    assert bandwidth_60.shape == (60, 60)
    assert bandwidth_64.shape == (64, 64)
    assert bandwidth_128.shape == (128, 128)
    # diagonals zero
    for M in (bandwidth_60, bandwidth_64, bandwidth_128):
        assert np.allclose(np.diag(M), 0.0)
    print("Shapes:", bandwidth_60.shape, bandwidth_64.shape, bandwidth_128.shape)
    outpath = Path(__file__).parent
    save_with_commas_and_brackets(outpath / "bandwidth_60.txt",  "bandwidth_60",  bandwidth_60)
    save_with_commas_and_brackets(outpath / "bandwidth_64.txt",  "bandwidth_64",  bandwidth_64)
    save_with_commas_and_brackets(outpath / "bandwidth_128.txt", "bandwidth_128", bandwidth_128)
    import json
    with open(outpath / "bandwidth_64.json", "w", encoding="utf-8") as f:
        for row in bandwidth_64:
            f.write(json.dumps(row.tolist(), ensure_ascii=False))
            f.write("\n")  # NOTE: comment translated from Chinese
